function identity_function(x) {
    return function(){
        return x
    }
}

function addf(x) {
    return function(y) {
        return x + y;
    }
}

function applyf(f) {
    return function(x) {
        return function(y) {
            return f(x, y);
        }
    }
}

function curry(f, x) {
    return function(y) {
        return f(x, y);
    }
}

function methodize(f) {
    return function(y) {
        return f(this, y);
    }
}

function demethodize(f) {
    return function(x, y) {
        return f.apply(x,[y]); // bind x to 'this'
    }
}

function twice(f) {
    return function (x) {
        return f(x, x);
    }
}

function composeu(f1, f2) {
    return function(x) {
        return f2(f1(x));
    }
}

function composeb(f1, f2) {
    return function(x, y, z) {
        return f2(f1(x, y), z);
    }
}

function once(f) {
    let not_used = true;
    return function(...x) {
        if(not_used) {
            not_used = false;
            return f(...x);
        }
        else {
            throw new Error("func was already called!");
        }
    }
}

function counterf(x) {
    let c = x;

    return {
        inc: function() {
            return ++c;
        },
        dec: function() {
            return --c;
        }
    }
}

function revocable(f) {
    let revoked = false;
    return {
        invoke: function(...x) {
            if(revoked) {
                throw new Error("function was revoked!")
            } else {
                return f(...x);
            }

        },
        revoke: function() {
            revoked = true;
        }
    }
}

function ArrayObject() {
    let array = [];

    return {
        get: function(i) {
            return array[i];
        },
        store: function(e, i) {
            array[i] = e;
        },
        append: function(e) {
            array.push(e);
        }
    }
}

